import React from 'react';
import Carlos from '../Images/imgcarlos.JPG';
const ProjectCard = ({ image, subtitle, demoLink, codeLink, description }) => (
    <div className="lg:w-1/3 sm:w-1/2 p-4">
      <div className="flex relative border border-blue-500 shadow-lg"> {/* Add shadow */}
        <img
          alt="gallery"
          className="absolute inset-0 w-full h-full object-cover object-center"
          src={image}
        />
        <div className="px-8 py-10 relative z-10 w-full border-4 border-gray-200 bg-white opacity-0 hover:opacity-100 transition duration-300 ease-in-out"> {/* Add transition */}
          <h2 className="tracking-widest text-sm title-font font-medium text-indigo-500 mb-1">
            {subtitle}
          </h2>
          <p className="leading-relaxed">
            {description}
          </p>
          <div className="pt-8 text-center">
            <a href={demoLink}>
              <button className="text-center rounded-lg px-4 py-3 m-2 bg-[#1172b6] text-white text-lg hover:bg-[#0a5d8a] transition duration-300 ease-in-out"> {/* Add hover effect */}
                Demo
              </button>
            </a>
            <a href={codeLink}>
              <button className="text-center rounded-lg px-4 py-3 m-2 bg-[#1172b6] text-white text-lg hover:bg-[#0a5d8a] transition duration-300 ease-in-out"> {/* Add hover effect */}
                Code
              </button>
            </a>
          </div>
        </div>
      </div>
    </div>
  );

function Work() {
  return (
    <div className="mt-[20rem] px-10 ">
      <h1 className="text-center text-2xl font-bold mb-4">My recent work </h1>
      <h2 className="text-center text-lg text-gray-600 mb-8">Here are a few past design projects I've worked on.  </h2>
      <div className="grid grid-cols-3 gap-4 border rounded-lg">
        {[...Array(6)].map((_, index) => (
          <div key={index} className="relative aspect-w-1 aspect-h-1 ">
            <h3 className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-lg font-bold text-white">Image {index + 1}</h3>
            <img src={Carlos} alt={`Image ${index + 1}`} className="absolute inset-0 object-cover w-full h-full transition-transform transform hover:scale-110" />
          </div>
        ))}
      </div>
    </div>
  );
}

export default Work;
